tinymce.PluginManager.add('removetablelines', function(editor, url) {

	editor.addButton('removetablelines', {
		text: 'Rensa tabellradsformatering',
		icon: false,
        tooltip: 'Rensa alla rader i tabellen som ej radbryter',
		onclick: function() {
			html = $.parseHTML(editor.getContent());
			
			var el = $(html);

			el.find("div.tableLine").each(function(index, item) {
				$(item).replaceWith($(item).html());
			});
			
			el.find("span.tabstop").remove();

			var text = el[0].outerHTML;

			editor.undoManager.transact(function() {
				editor.setContent(text);
			});

		}
	});

});