tinymce.PluginManager.add('removetableparagraphs', function(editor, url) {

	editor.addButton('removetableparagraphs', {
		text: 'Rensa tabellstycken',
		icon: false,
        tooltip: 'Ta bort all styckeformatering fr√•n tabeller',
		onclick: function() {
			html = $.parseHTML(editor.getContent());
			
			var el = $(html);

			el.find("td p").each(function(index, item) {
				$(item).replaceWith($(item).html());
			});

			var text = el[0].outerHTML;

			editor.undoManager.transact(function() {
				editor.setContent(text);
			});

		}
	});

});